import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence, useAnimation } from 'framer-motion';
import { Star, Menu, X } from 'lucide-react';
import { useTranslation } from 'react-i18next';

// Updated models array
const models = [
  { id: 'iphone13', name: 'iPhone 13' },
  { id: 'iphone13mini', name: 'iPhone 13 Mini' },
  { id: 'iphone13pro', name: 'iPhone 13 Pro' },
  { id: 'iphone13promax', name: 'iPhone 13 Pro Max' },
  { id: 'iphone14', name: 'iPhone 14' },
  { id: 'iphone14plus', name: 'iPhone 14 Plus' },
  { id: 'iphone14pro', name: 'iPhone 14 Pro' },
  { id: 'iphone14promax', name: 'iPhone 14 Pro Max' },
  { id: 'iphone15', name: 'iPhone 15' },
  { id: 'iphone15plus', name: 'iPhone 15 Plus' },
  { id: 'iphone15pro', name: 'iPhone 15 Pro' },
  { id: 'iphone15promax', name: 'iPhone 15 Pro Max' },
];

export default function AuraCasesLanding() {
  const { t, i18n } = useTranslation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [selectedModel, setSelectedModel] = useState(models[0].id);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isHovering, setIsHovering] = useState(false);
  const controls = useAnimation();

  // State for "Back to Top" button
  const [showScrollToTop, setShowScrollToTop] = useState(false);

  // State for scroll progress indicator
  const [scrollProgress, setScrollProgress] = useState(0);

  // Carousel effect for Hero section
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) =>
        prevIndex === productImages.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  // Control for testimonials carousel
  useEffect(() => {
    const animateCarousel = async () => {
      await controls.start({
        x: '-100%',
        transition: {
          duration: 20,
          ease: 'linear',
          repeat: Infinity,
        },
      });
    };
    animateCarousel();
  }, [controls]);

  // Handle scroll events for "Back to Top" button and progress indicator
  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset;
      const docHeight = document.body.scrollHeight - window.innerHeight;
      const scrollPercent = (scrollTop / docHeight) * 100;
      setScrollProgress(scrollPercent);

      if (scrollTop > 300) {
        setShowScrollToTop(true);
      } else {
        setShowScrollToTop(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleMouseEnter = () => {
    setIsHovering(true);
    controls.stop();
    controls.start({
      x: '-100%',
      transition: {
        duration: 40,
        ease: 'linear',
        repeat: Infinity,
      },
    });
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
    controls.stop();
    controls.start({
      x: '-100%',
      transition: {
        duration: 20,
        ease: 'linear',
        repeat: Infinity,
      },
    });
  };

  // Function to handle navigation clicks with smooth scrolling
  const handleNavClick = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
    setMobileMenuOpen(false); // Close mobile menu if open
  };

  // Fetch testimonials from translations
  const testimonials = t('testimonials', { returnObjects: true });

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900">
      {/* Scroll Progress Indicator */}
      <div className="fixed top-0 left-0 w-full h-1 bg-transparent z-50">
        <motion.div
          className="h-full bg-gray-900"
          style={{ width: `${scrollProgress}%` }}
        />
      </div>

      {/* Header */}
      <header className="bg-white shadow-md fixed w-full z-40">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold">
            <span className="text-gray-800">Aura</span>
            <span className="text-gray-600">Cases</span>
          </h1>
          <nav className="hidden md:flex space-x-6 items-center">
            <span
              onClick={() => handleNavClick('products')}
              className="cursor-pointer hover:text-gray-600 transition"
            >
              {t('Product')}
            </span>
            <span
              onClick={() => handleNavClick('features')}
              className="cursor-pointer hover:text-gray-600 transition"
            >
              {t('Features')}
            </span>
            <span
              onClick={() => handleNavClick('testimonials')}
              className="cursor-pointer hover:text-gray-600 transition"
            >
              {t('Testimonials')}
            </span>
            <span
              onClick={() => handleNavClick('contact')}
              className="cursor-pointer hover:text-gray-600 transition"
            >
              {t('Contact')}
            </span>
            {/* Language Toggle Button */}
            <motion.button
              onClick={() => {
                const newLang = i18n.language === 'ro' ? 'ru' : 'ro';
                i18n.changeLanguage(newLang);
              }}
              className="ml-4 bg-gray-200 text-gray-800 px-3 py-1 rounded-full focus:outline-none"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              {i18n.language === 'ro' ? 'RU' : 'RO'}
            </motion.button>
          </nav>
          <button
            className="md:hidden focus:outline-none"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          >
            {mobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>
        <AnimatePresence>
          {mobileMenuOpen && (
            <motion.div
              className="md:hidden bg-white shadow-md"
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.3 }}
            >
              <span
                onClick={() => handleNavClick('products')}
                className="block px-4 py-2 hover:bg-gray-100 cursor-pointer"
              >
                {t('Product')}
              </span>
              <span
                onClick={() => handleNavClick('features')}
                className="block px-4 py-2 hover:bg-gray-100 cursor-pointer"
              >
                {t('Features')}
              </span>
              <span
                onClick={() => handleNavClick('testimonials')}
                className="block px-4 py-2 hover:bg-gray-100 cursor-pointer"
              >
                {t('Testimonials')}
              </span>
              <span
                onClick={() => handleNavClick('contact')}
                className="block px-4 py-2 hover:bg-gray-100 cursor-pointer"
              >
                {t('Contact')}
              </span>
              {/* Mobile Language Toggle Button */}
              <motion.button
                onClick={() => {
                  const newLang = i18n.language === 'ro' ? 'ru' : 'ro';
                  i18n.changeLanguage(newLang);
                }}
                className="mt-2 mx-4 bg-gray-200 text-gray-800 px-3 py-1 rounded-full focus:outline-none"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                {i18n.language === 'ro' ? 'RU' : 'RO'}
              </motion.button>
            </motion.div>
          )}
        </AnimatePresence>
      </header>

      {/* Space for fixed header */}
      <div className="pt-20"></div>

      {/* Hero Section */}
      {/* ... (Rest of the component remains mostly unchanged) */}

      {/* Back to Top Button */}
      {showScrollToTop && (
        <motion.button
          className="fixed bottom-8 right-8 bg-gray-900 text-white p-3 rounded-full shadow-lg"
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          â†‘
        </motion.button>
      )}
    </div>
  );
}
