import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence, useAnimation } from 'framer-motion';
import { Star, Menu, X } from 'lucide-react';
import { useTranslation } from 'react-i18next';
// If using flag icons, import them here
// import RoFlag from './assets/ro-flag.svg';
// import RuFlag from './assets/ru-flag.svg';

const productImages = [
  'https://images.rawpixel.com/image_800/cHJpdmF0ZS9sci9pbWFnZXMvd2Vic2l0ZS8yMDIzLTA4L3Jhd3BpeGVsb2ZmaWNlOV9waG90b2dyYXBoX2FfbWluaW1hbGlzdF93aGl0ZV9zbWFydHBob25lX2Nhc2Vfc183ZmFkODY1NS03ZDBjLTQ4NmMtOGFkNy04OWEzNjNmNzcwZGNfMS5qcGc.jpg',
  'https://www.uniqfind.com/cdn/shop/files/woven-iphone-case-2_4d33cf11-6c95-4e10-8f86-209070a57be8_1200x.jpg?v=1692631605',
  'https://as-images.apple.com/is/MM2A3?wid=1000&hei=1000&fmt=jpeg&qlt=95&.v=1629924876000',
  'https://en.woodcessories.com/cdn/shop/files/i12pro_Bumper_Case_stein_Nachhaltig_10261ea4-dec3-4acf-9813-a34a9698e76c.webp?v=1687949912',
];

const models = [
  { id: 'iphone13', name: 'iPhone 13' },
  { id: 'iphone14', name: 'iPhone 14' },
  { id: 'iphone15', name: 'iPhone 15' },
];

const features = [
  {
    title: 'Unique Design',
    description: 'Customizable e-ink display for a multitude of options.',
    icon: '🎨',
  },
  {
    title: 'Durability',
    description: 'Premium materials for ultimate phone protection.',
    icon: '🛡️',
  },
  {
    title: 'Easy to Use',
    description: 'Intuitive interface for easy customization.',
    icon: '📱',
  },
];

const testimonials = [
  {
    name: 'Alex M.',
    content: 'AuraCases transformed my phone! The e-ink display is revolutionary.',
    rating: 5,
  },
  {
    name: 'Sarah L.',
    content: 'Elegant, durable, and unique. Exactly what I was looking for!',
    rating: 5,
  },
  {
    name: 'Mike T.',
    content: 'Excellent quality and unlimited customization options. Highly recommend!',
    rating: 4,
  },
  {
    name: 'Ion B.',
    content: 'Local products are top-notch! Very satisfied with AuraCases.',
    rating: 5,
  },
  {
    name: 'Elena C.',
    content: 'Excellent services and superior quality product!',
    rating: 5,
  },
  {
    name: 'Mihai D.',
    content: 'Modern design and impeccable functionality.',
    rating: 5,
  },
];

export default function AuraCasesLanding() {
  const { t, i18n } = useTranslation(); // Destructure i18n
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [selectedModel, setSelectedModel] = useState(models[0].id);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isHovering, setIsHovering] = useState(false);
  const controls = useAnimation();

  // Carousel effect for Hero section
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) =>
        prevIndex === productImages.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval);
  }, []);

  // Control for testimonials carousel
  useEffect(() => {
    const animateCarousel = async () => {
      await controls.start({
        x: '-100%',
        transition: {
          duration: 20,
          ease: 'linear',
          repeat: Infinity,
        },
      });
    };
    animateCarousel();
  }, [controls]);

  const handleMouseEnter = () => {
    setIsHovering(true);
    controls.stop();
    controls.start({
      x: '-100%',
      transition: {
        duration: 40, // Slow down carousel
        ease: 'linear',
        repeat: Infinity,
      },
    });
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
    controls.stop();
    controls.start({
      x: '-100%',
      transition: {
        duration: 20,
        ease: 'linear',
        repeat: Infinity,
      },
    });
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900">
      {/* Header */}
      <header className="bg-white shadow-md fixed w-full z-50">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold">
            <span className="text-gray-800">Aura</span>
            <span className="text-gray-600">Cases</span>
          </h1>
          <nav className="hidden md:flex space-x-6 items-center">
            <a href="#products" className="hover:text-gray-600 transition">
              {t('Product')}
            </a>
            <a href="#features" className="hover:text-gray-600 transition">
              {t('Features')}
            </a>
            <a href="#testimonials" className="hover:text-gray-600 transition">
              {t('Testimonials')}
            </a>
            <a href="#contact" className="hover:text-gray-600 transition">
              {t('Contact')}
            </a>
            {/* Language Toggle Button */}
            <motion.button
              onClick={() => {
                const newLang = i18n.language === 'ro' ? 'ru' : 'ro';
                i18n.changeLanguage(newLang);
              }}
              className="ml-4 bg-gray-200 text-gray-800 px-3 py-1 rounded-full focus:outline-none"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              {/* Replace with flag icons if available */}
              {i18n.language === 'ro' ? 'RU' : 'RO'}
            </motion.button>
          </nav>
          <button
            className="md:hidden focus:outline-none"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          >
            {mobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>
        <AnimatePresence>
          {mobileMenuOpen && (
            <motion.div
              className="md:hidden bg-white shadow-md"
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.3 }}
            >
              <a href="#products" className="block px-4 py-2 hover:bg-gray-100">
                {t('Product')}
              </a>
              <a href="#features" className="block px-4 py-2 hover:bg-gray-100">
                {t('Features')}
              </a>
              <a href="#testimonials" className="block px-4 py-2 hover:bg-gray-100">
                {t('Testimonials')}
              </a>
              <a href="#contact" className="block px-4 py-2 hover:bg-gray-100">
                {t('Contact')}
              </a>
              {/* Mobile Language Toggle Button */}
              <motion.button
                onClick={() => {
                  const newLang = i18n.language === 'ro' ? 'ru' : 'ro';
                  i18n.changeLanguage(newLang);
                }}
                className="mt-2 mx-4 bg-gray-200 text-gray-800 px-3 py-1 rounded-full focus:outline-none"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                {/* Replace with flag icons if available */}
                {i18n.language === 'ro' ? 'RU' : 'RO'}
              </motion.button>
            </motion.div>
          )}
        </AnimatePresence>
      </header>

      {/* Space for fixed header */}
      <div className="pt-20"></div>

      {/* Hero Section */}
      <section className="bg-white py-20">
        <div className="container mx-auto px-4 flex flex-col md:flex-row items-center">
          {/* Text Content */}
          <motion.div
            className="md:w-1/2"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h2 className="text-4xl md:text-5xl font-bold mb-4">
              {t('Personalize your iPhone experience')}
            </h2>
            <p className="text-lg text-gray-700 mb-6">
              {t(
                'Discover ultimate protection with AuraCases, our cases with customizable e-ink display. Match your case to your style effortlessly.'
              )}
            </p>
            <p className="text-2xl font-bold text-gray-800 mb-6">
              {t('Price')}: 699 MDL
            </p>
            <div className="flex items-center space-x-4">
              <motion.button
                className="bg-gray-900 text-white px-6 py-3 rounded-full text-lg font-semibold hover:bg-gray-700 transition duration-300"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() =>
                  document.getElementById('products').scrollIntoView({ behavior: 'smooth' })
                }
              >
                {t('Order Now')}
              </motion.button>
              <div className="flex items-center">
                <img
                  src="https://w1.pngwing.com/pngs/236/69/png-transparent-flag-moldova-flag-of-moldova-national-flag-coat-of-arms-of-moldova-yellow-area-logo.png"
                  alt={t('Moldova Flag')}
                  className="w-10 h-10"
                />
                <span className="ml-2 text-gray-700">{t('Made in Moldova')}</span>
              </div>
            </div>
          </motion.div>

          {/* Image Carousel */}
          <motion.div
            className="md:w-1/2 mb-8 md:mb-0"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <div className="relative w-full h-96">
              <AnimatePresence>
                <motion.img
                  key={currentImageIndex}
                  src={productImages[currentImageIndex]}
                  alt="AuraCases E-ink Case for iPhone"
                  className="absolute top-0 left-0 w-full h-full object-contain rounded-lg shadow-lg"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  exit={{ opacity: 0 }}
                  transition={{ duration: 1 }}
                />
              </AnimatePresence>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Product Presentation Section */}
      <section id="products" className="py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-12 text-center">
            {t('Our Product')}
          </h2>
          <div className="flex flex-col md:flex-row items-center justify-center mb-12">
            {/* Product Image */}
            <motion.div
              className="md:w-1/2 mb-6 md:mb-0 order-2 md:order-1"
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.8 }}
            >
              <img
                src={productImages[currentImageIndex]}
                alt="AuraCases E-ink Case for iPhone"
                className="w-full h-auto rounded-lg shadow-lg"
              />
            </motion.div>

            {/* Customization Options */}
            <motion.div
              className="md:w-1/2 md:pl-12 order-1 md:order-2"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.3 }}
            >
              <div className="mb-4">
                <label htmlFor="model" className="block text-lg font-semibold mb-2">
                  {t('Select your iPhone Model')}
                </label>
                <div className="relative">
                  <select
                    id="model"
                    className="w-full border border-gray-300 rounded-full px-4 py-2 appearance-none focus:outline-none focus:ring-2 focus:ring-gray-500"
                    value={selectedModel}
                    onChange={(e) => setSelectedModel(e.target.value)}
                  >
                    {models.map((model) => (
                      <option key={model.id} value={model.id}>
                        {model.name}
                      </option>
                    ))}
                  </select>
                  <span className="absolute right-4 top-1/2 transform -translate-y-1/2 pointer-events-none text-gray-500">
                    ▼
                  </span>
                </div>
              </div>
              <div className="mb-4">
                <p className="text-lg font-semibold mb-2">
                  {t('Customize Your Case')}
                </p>
                <p className="text-gray-700 mb-4">
                  {t(
                    'After purchase, you can customize the e-ink display with your favorite designs, messages, or images.'
                  )}
                </p>
                <motion.button
                  className="bg-gray-900 text-white px-6 py-3 rounded-full text-lg font-semibold hover:bg-gray-700 transition duration-300"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => alert(t('Purchase initiated!'))}
                >
                  {t('Order Now')}
                </motion.button>
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="py-20">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-12 text-center">
            {t('Why Choose AuraCases?')}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {features.map((feature, index) => (
              <motion.div
                key={index}
                className="bg-white rounded-lg shadow-md p-6 text-center"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
              >
                <div className="text-4xl mb-4">{feature.icon}</div>
                <h3 className="text-xl font-semibold mb-2">{t(feature.title)}</h3>
                <p className="text-gray-600">{t(feature.description)}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Testimonials Section */}
      <section id="testimonials" className="py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-12 text-center">
            {t('Testimonials')}
          </h2>
          <motion.div
            className="overflow-hidden relative"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            <motion.div
              className="flex space-x-8 items-stretch"
              animate={controls}
              initial={{ x: 0 }}
            >
              {[...testimonials, ...testimonials].map((testimonial, index) => (
                <div
                  key={index}
                  className="bg-white rounded-lg shadow-md p-6 flex flex-col justify-between min-h-[300px] flex-shrink-0"
                >
                  <p className="text-gray-700 mb-4 line-clamp-2">"{testimonial.content}"</p>
                  <div className="flex items-center justify-between">
                    <span className="font-semibold">{testimonial.name}</span>
                    <div className="flex">
                      {[...Array(testimonial.rating)].map((_, i) => (
                        <Star key={i} className="w-5 h-5 fill-yellow-400 stroke-yellow-400" />
                      ))}
                    </div>
                  </div>
                </div>
              ))}
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Newsletter Subscription Section */}
      <section id="contact" className="bg-white py-20">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl font-bold mb-4">{t('Stay Updated')}</h2>
          <p className="mb-8 text-gray-700">
            {t('Subscribe to our newsletter for the latest news and exclusive offers.')}
          </p>
          <form className="max-w-md mx-auto flex flex-col sm:flex-row items-center">
            <input
              type="email"
              placeholder={t('Enter your email address')}
              className="w-full sm:flex-grow px-4 py-2 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-gray-500 mb-4 sm:mb-0 sm:mr-4"
              required
            />
            <motion.button
              type="submit"
              className="bg-gray-900 text-white px-6 h-12 rounded-full font-semibold hover:bg-gray-700 transition duration-300 flex items-center justify-center"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              {t('Subscribe')}
            </motion.button>
          </form>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-gray-100 py-8">
        <div className="container mx-auto px-4">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <div className="text-center md:text-left mb-4 md:mb-0">
              <h3 className="text-xl font-semibold">AuraCases</h3>
              <p className="text-gray-600">
                © {new Date().getFullYear()} AuraCases. {t('All rights reserved')}.
              </p>
              <p className="text-gray-600">{t('Made in Moldova')}.</p>
            </div>
            <div className="flex space-x-4">
              {/* Social Media Icons */}
              {/* Add your social media icons here */}
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}
